mapper.select:
SELECT *from (SELECT c.ctid,c.ctname,c.ctcontext,c.cttime, cos.cosid,cos.cosname,cos.cosdeta,cos.costime,cos.coshot, tblfc.fcid,tblfc.fid, f.facc,f.fpwd,f.fname,f.flaw,f.fphone,f.flawphone,f.fsite,f.ftime,f.rid, sf.sfid,sf.sfname,sf.sfexp,sf.sfdob,sf.sfcos,sf.sfage,sf.sftag,sf.sfworkexp,sf.sftrain,sf.sfwant,sf.sfgood,sf.sfedu,sf.sfurl FROM tblCOStype AS c INNER JOIN tblCOS AS cos ON cos.ctid = c.ctid INNER JOIN tblfirm AS f ON f.ctid = c.ctid INNER JOIN tblstaff AS sf ON sf.cosid = cos.cosid AND sf.fid = f.fid INNER JOIN tblfc ON tblfc.ctid = c.ctid AND tblfc.fid = f.fid)m
WHERE ctname LIKE "%"#{aunt}"%" OR ctcontext LIKE "%"#{aunt}"%" OR cosname LIKE "%"#{aunt}"%" OR cosdeta LIKE "%"#{aunt}"%" OR fname LIKE "%"#{aunt}"%" OR sfname LIKE "%"#{aunt}"%" OR sftag LIKE "%"#{aunt}"%"
limit #{page},#{limit}
